/* eslint-disable no-underscore-dangle */
import { create } from 'zustand';
export const useHeaderActionStore = create((set, get) => ({
    setAction: (element) => {
        set(() => ({
            element,
        }));
        return get().removeAction;
    },
    removeAction: () => {
        set(() => ({
            element: null,
        }));
    },
}));
